-- ----------------------------
-- View structure for V_GEN_EXCH_ACQUIRE
-- ----------------------------
CREATE OR REPLACE VIEW "ESPLATFORM"."V_GEN_EXCH_ACQUIRE" AS SELECT
 T.ORGCODE,GETSYSORGNAMEDETAIL(T.ORGCODE) AS ORGNAME,  SUM(T.CHECKCOUNT) NUM ,GETRESNAMEBYCODE(T.RECCODE) AS RESTYPE, '01' AS TYPE,T.CTRTIME  FROM CENTACQUIRE T   GROUP BY ORGCODE,RECCODE,CTRTIME
UNION ALL
SELECT T.ORGCODE,GETSYSORGNAMEDETAIL(T.ORGCODE) AS ORGNAME,   COUNT(T.BATCHNO) NUM ,GETRESNAMEBYCODE(T.FILERESCODE) AS RESTYPE, '02' AS TYPE,T.CTRTIME  FROM CENTFILEACQUIRE T   GROUP BY ORGCODE,FILERESCODE,CTRTIME;

